import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'

export default class DashboardMovieRow extends React.Component {
	constructor(props) {
		super(props);
	}

	/* ---- Q1b (Dashboard) ---- */
	/* Change the contents (NOT THE STRUCTURE) of the HTML elements to show a movie row. */

	submitLocation(lat, lon) {
	fetch(`http://localhost:8081/zones/${lat}/${lon}`, {
    method: 'GET' // The type of HTTP request.
	})
	.then(res => res.json())
	.then(zoneList => {
		if(!zoneList) return;
		console.log(zoneList);
	})
	}

	submitPOIS(lat, lon, distance) {
		console.log("DISTANCE: " + distance)
		fetch(`http://localhost:8081/pois/${lat}/${lon}/${distance}`, {
			method: 'GET'
		})
		.then(res => res.json())
		.then(POIList => {
			if(!POIList) return;
			let pois = POIList.map((poiObj, i) => {
				console.log(poiObj);
			})
		})
	}

	render() {
		return (
			<>
			<div className="location">
				<div className="name">{this.props.name}</div>
			</div>
			<button type="button" class="btn btn-primary" onClick={() => this.submitPOIS(this.props.lat, this.props.lon, this.props.dist)} >Select</button>
			</>
		);
	}
}
