import React from 'react';
import '../style/Dashboard.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import PageNavbar from './PageNavbar';
import GenreButton from './GenreButton';
import DashboardMovieRow from './DashboardMovieRow';

export default class Dashboard extends React.Component {
  constructor(props) {
    super(props);

    // The state maintained by this React Component. This component maintains the list of genres,
    // and a list of movies for a specified genre.
    this.state = {
      locationName: "",
      genres: [],
      movies: []
    }

    this.showMovies = this.showMovies.bind(this);
    this.handleLocationNameChange = this.handleLocationNameChange.bind(this);
    this.submitLocation = this.submitLocation.bind(this);
  }

  handleLocationNameChange(e) {
    this.setState({
      locationName: e.target.value
    });
  }

  submitLocation() {
    fetch(`https://nominatim.openstreetmap.org/search?${this.state.locationName}`, {
      method: 'GET'
    })
    .then(res => res.json())
    .then(locationList => {
      if (!locationList) return;
      console.log(locationList)
    })
    .catch(err => console.log(err))
  }

  // React function that is called when the page load.
  componentDidMount() {
    // Send an HTTP request to the server.
    fetch("http://localhost:8081/genres", {
      method: 'GET' // The type of HTTP request.
    })
      .then(res => res.json()) // Convert the response data to a JSON.
      .then(genreList => {
        if (!genreList) return;
        // Map each genreObj in genreList to an HTML element:
        // A button which triggers the showMovies function for each genre.
        let genreDivs = genreList.map((genreObj, i) =>
          <GenreButton id={"button-" + genreObj.genre} onClick={() => this.showMovies(genreObj.genre)} genre={genreObj.genre} />
        );

        // Set the state of the genres list to the value returned by the HTTP response from the server.
        this.setState({
          genres: genreDivs
        })
      })
      .catch(err => console.log(err))	// Print the error if there is one.
  }


  /* ---- Q1b (Dashboard) ---- */
  /* Set this.state.movies to a list of <DashboardMovieRow />'s. */
  showMovies(genre) {
        // Send an HTTP request to the server.
    fetch(`http://localhost:8081/genres/${genre}`, {
      method: 'GET' // The type of HTTP request.
    })
      .then(res => res.json()) // Convert the response data to a JSON.
      .then(movieList => {
        if (!movieList) return;
        let movieDivs = movieList.map((movieObj, i) =>
          <DashboardMovieRow title={movieObj.title} rating={movieObj.rating} voteCount={movieObj.vote_count}/>
        );
        this.setState({
          movies: movieDivs
        })
      })
      .catch(err => console.log(err))	// Print the error if there is one.

  }

  render() {    
    return (
      <div className="Dashboard">

        <PageNavbar active="dashboard" />

        <br></br>
        <div className="container movies-container">
          <div className="jumbotron">
          <div className="h5">Trip Planner</div>
           <div className="location-input">
             <input type="text" placeholder="Enter a location" value={this.state.locationName} onChange={this.handleLocationNameChange} id="location-input" className="location-input"/>
             <button type="button" class="btn btn-primary" onClick={this.submitLocation}>Search</button>
           </div>
          </div>

          <br></br>
          <div className="jumbotron">
            <div className="movies-container">
              <div className="movies-header">
                <div className="header-lg"><strong>Title</strong></div>
                <div className="header"><strong>Rating</strong></div>
                <div className="header"><strong>Vote Count</strong></div>
              </div>
              <div className="results-container" id="results">
                {this.state.movies}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
